<?php/** * @file views-view-unformatted.tpl.php * Default simple view template to display a list of rows. * * @ingroup views_templates */ $first = TRUE; ?><?php if (!empty($title)): ?>  <h3><?php print $title; ?></h3><?php endif; ?>  <div id="media-gallery-carousel" class="carousel slide">    <ol class="carousel-indicators">    <?php     foreach ($rows as $id => $row):       print '<li data-target="#media-gallery-carousel" data-slide-to="' . $id . '" class="' . ($first ? "active" : "") . '"></li>';                $first = FALSE;     endforeach; ?>    </ol>    <?php $first = TRUE; ?>    <div class="carousel-inner"><?php foreach ($rows as $id => $row): ?>  <div class="<?php print $classes_array[$id]; ?> <?php print ($first ? 'active' : '') ?>">    <?php       //Check if the galleries are actually empty      $row = str_replace('[Empty_gallery][Empty_gallery]', '', $row );      //Check if there is only one picture      $row = str_replace('[Empty_gallery]', '', $row );      print $row;       $first = FALSE;    ?>  </div><?php endforeach; ?>    </div>    <a class="left carousel-control" href="#media-gallery-carousel" data-slide="prev"><span class="glyphicon glyphicon-chevron-left"></span></a>    <a class="right carousel-control" href="#media-gallery-carousel" data-slide="next"><span class="glyphicon glyphicon-chevron-right"></span></a>      </div><!-- Small bits of jquery to make it clean --><script type="text/javascript">  //use jQuery 1.7.1  (function($){    $(document).ready(function() {  //Once the page elements are fully loaded      $row=$('div.carousel-inner div.views-row');      //hide the video thumbnails in galleries with pictur thumbnails      $row.each(function() {       if($(this).find('a').size()>2){        $(this).find('a:last').hide();        }      });    });  })(jq171); </script>